    /**
     * @notice Get badge type information
     */
    function getBadgeTypeInfo(uint256 badgeTypeId)
        external
        view
        badgeTypeExists(badgeTypeId)
        returns (
            string memory name,
            string memory description,
            uint256 requiredPoints,
            string memory category,
