pragma solidity ^0.8.24;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {IDepositCertificate} from "./interfaces/IDepositCertificate.sol";

/// @title DepositCertificateUpgradeable
/// @notice ERC721 upgradable đại diện cho các sổ tiết kiệm (deposit certificates).
/// Mỗi depositId được map 1-1 với tokenId.
contract DepositCertificateUpgradeable is
    Initializable,
    ERC721Upgradeable,
    OwnableUpgradeable,
    IDepositCertificate
{
    /// @notice Địa chỉ SavingBank executable    được phép mint/burn.
    address public savingBankExecutable;

    error NotSavingBankExecutable();
    error ZeroAddress();

    /// @dev Initializer thay cho constructor.
    function initialize(
        string memory name_,
        string memory symbol_,
        address owner_
    ) public initializer {
        if (owner_ == address(0)) {
            revert ZeroAddress();
        }
        __ERC721_init(name_, symbol_);
        __Ownable_init(owner_);
    }

    /// @notice Thiết lập địa chỉ SavingBank executable được phép mint/burn.
    /// Có thể set một lần sau khi deploy; nếu muốn đổi cần review security.
    function setBankExecutable(address executable) external onlyOwner {
        if (executable == address(0)) {
            revert ZeroAddress();
        }
        savingBankExecutable = executable;
    }

    modifier onlySavingBankExecutable() {
        if (msg.sender != savingBankExecutable) {
            revert NotSavingBankExecutable();
        }
        _;
    }

    /// @inheritdoc IDepositCertificate
    function mintCertificate(address to, uint256 depositId)
        external
        override
        onlySavingBankExecutable
    {
        _safeMint(to, depositId);
    }

    /// @inheritdoc IDepositCertificate
    function burnCertificate(uint256 depositId)
        external
        override
        onlySavingBankExecutable
    {
        _burn(depositId);
    }

    function ownerDepositCertificateOf(uint256 tokenId) 
        external 
        view 
        override
        returns (address) {
        return ownerOf(tokenId);
    }
}","keccak256":"0xc9b9aff7af32d75da306c0cb39f3bf03e02ef5deb7c4c43434b3783f929bd2de","license":"MIT"},"contracts/DepositRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {IDepositRegistry} from "./interfaces/IDepositRegistry.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract DepositRegistry is IDepositRegistry, Ownable {
    uint256 public _nextDepositId = 1;

    mapping(uint256 => DepositInfo) private _deposits;
    mapping(address => bool) public isBankExecutable;

    // Hỗ trợ nhiều sổ active cho 1 user
    mapping(address => uint256[]) private _activeDepositsOf;

    error NotAuthorizedExecutable();
    error InvalidOwner();
    error InvalidDeposit();
    error DepositNotActive();

    modifier onlyExecutable() {
        if (!isBankExecutable[msg.sender]) revert NotAuthorizedExecutable();
        _;
    }

    constructor() Ownable(msg.sender) {}

    // ---------------------------------------------------------------------
    // Admin
    // ---------------------------------------------------------------------

    function setBankExecutable(address executable, bool allowed) external onlyOwner {
        isBankExecutable[executable] = allowed;
    }

    // ---------------------------------------------------------------------
    // Views - Implement đầy đủ từ interface
    // ---------------------------------------------------------------------

    function nextDepositId() external view override returns (uint256) {
        return _nextDepositId;
    }

    function deposits(uint256 depositId) external view override returns (DepositInfo memory) {
        return _deposits[depositId];
    }

    function activeDepositOf(address user) external view override returns (uint256[] memory) {
        return _activeDepositsOf[user];
    }

    function getActiveDepositId(address user) external view override returns (uint256[] memory) {
        return _activeDepositsOf[user];
    }

    // ---------------------------------------------------------------------
    // Executable write functions - Implement đầy đủ
    // ---------------------------------------------------------------------

    function createDeposit(
        address owner,
        uint256 planId,
        uint256 principal,
        uint32 tenorDays,
        uint32 aprBps,
        uint32 earlyWithdrawPenaltyBps,
        uint64 startAt,
        uint64 maturityAt
    ) external override onlyExecutable returns (uint256 depositId) {
        if (owner == address(0)) revert InvalidOwner();

        depositId = _nextDepositId++;
        _deposits[depositId] = DepositInfo({
            id: depositId,
            planId: planId,
            owner: owner,
            principal: principal,
            tenorDays: tenorDays,
            aprBps: aprBps,
            earlyWithdrawPenaltyBps: earlyWithdrawPenaltyBps,
            startAt: startAt,
            maturityAt: maturityAt,
            status: DepositStatus.Active
        });

        // Thêm vào danh sách active của user
        _activeDepositsOf[owner].push(depositId);
    }

    function markWithdrawn(uint256 depositId) external override onlyExecutable {
        DepositInfo storage dep = _deposits[depositId];
        if (dep.id == 0 || dep.owner == address(0)) revert InvalidDeposit();
        if (dep.status != DepositStatus.Active) revert DepositNotActive();

        dep.status = DepositStatus.Withdrawn;
        _removeFromActive(dep.owner, depositId);
    }

    function markEarlyWithdrawn(uint256 depositId) external override onlyExecutable {
        DepositInfo storage dep = _deposits[depositId];
        if (dep.id == 0 || dep.owner == address(0)) revert InvalidDeposit();
        if (dep.status != DepositStatus.Active) revert DepositNotActive();

        dep.status = DepositStatus.EarlyWithdrawn;
        _removeFromActive(dep.owner, depositId);
    }

    function markRenewed(uint256 depositId) external override onlyExecutable {
        DepositInfo storage dep = _deposits[depositId];
        if (dep.id == 0 || dep.owner == address(0)) revert InvalidDeposit();
        if (dep.status != DepositStatus.Active) revert DepositNotActive();

        dep.status = DepositStatus.Renewed;
        _removeFromActive(dep.owner, depositId);
    }

    function createRenewedDeposit(
        address owner,
        uint256 newPlanId,
        uint256 newPrincipal,
        uint32 tenorDays,
        uint32 aprBps,
        uint32 earlyWithdrawPenaltyBps,
        uint64 startAt,
        uint64 maturityAt
    ) external override onlyExecutable returns (uint256 newDepositId) {
        if (owner == address(0)) revert InvalidOwner();

        newDepositId = _nextDepositId++;
        _deposits[newDepositId] = DepositInfo({
            id: newDepositId,
            planId: newPlanId,
            owner: owner,
            principal: newPrincipal,
            tenorDays: tenorDays,
            aprBps: aprBps,
            earlyWithdrawPenaltyBps: earlyWithdrawPenaltyBps,
            startAt: startAt,
            maturityAt: maturityAt,
            status: DepositStatus.Active
        });

        // Thêm sổ mới vào danh sách active
        _activeDepositsOf[owner].push(newDepositId);
    }

    // Helper private: xóa depositId khỏi mảng active (efficient swap & pop)
    function _removeFromActive(address user, uint256 depositId) private {
        uint256[] storage ids = _activeDepositsOf[user];
        for (uint256 i = 0; i < ids.length; i++) {
            if (ids[i] == depositId) {
                ids[i] = ids[ids.length - 1];
                ids.pop();
                break;
            }
        }
    }
}","keccak256":"0xd1d6b9f6d371b47e43c545fb87360f5edb3f2d9e87dee5622600406c44ea6a52","license":"MIT"},"contracts/SavingBankUpgradeable.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IDepositCertificate} from "./interfaces/IDepositCertificate.sol";
import {IDepositRegistry} from "./interfaces/IDepositRegistry.sol";
import {ISavingBankUpgradeable} from "./interfaces/ISavingBankUpgradeable.sol";
import {Vault} from "./Vault.sol"; // Import contract Vault riêng

/// @title SavingBankUpgradeable
/// @notice Core logic sản phẩm tiết kiệm có kỳ hạn, tách riêng vault ra contract Vault.
/// Chỉ owner và Vault mới được phép nạp/rút tiền lãi.
contract SavingBankUpgradeable is
    Initializable,
    OwnableUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable,
    ISavingBankUpgradeable
{
    using SafeERC20 for IERC20;

    /// @dev Maximum APR in basis points (e.g. 5000 = 50%).
    uint32 public constant MAX_APR_BPS = 5000;

    /// @dev Maximum penalty in basis points (10000 = 100%).
    uint32 public constant MAX_PENALTY_BPS = 10000;

    IERC20 public token;
    IDepositRegistry public registry;
    IDepositCertificate public certificate;
    Vault public vault; // ← Vault riêng thay vì uint256 vaultBalance

    /// @notice Address receiving early-withdrawal penalties (can be zero address => penalties stay in vault).
    address public feeReceiver;

    uint256 public nextPlanId;

    struct SavingPlan {
        uint256 id;
        uint32 tenorDays;
        uint32 aprBps;
        uint256 minDeposit;
        uint256 maxDeposit; // 0 = unlimited
        uint32 earlyWithdrawPenaltyBps;
        bool enabled;
    }

    mapping(uint256 => SavingPlan) public plans;

    // ---------------------------------------------------------------------
    // Errors
    // ---------------------------------------------------------------------

    error ZeroAddress();
    error InvalidPlan();
    error InvalidDeposit();
    error PlanDisabled();
    error InvalidAmount();
    error InvalidParameters();
    error NotDepositOwner();
    error DepositNotActive();
    error NotMatured();
    error AlreadyMatured();
    error InsufficientVault();

    // ---------------------------------------------------------------------
    // Initializer
    // ---------------------------------------------------------------------

    /// @notice Khởi tạo contract upgradable.
    /// @param token_ Địa chỉ ERC20 dùng làm stablecoin.
    /// @param registry_ Địa chỉ DepositRegistry lưu state deposit.
    /// @param certificate_ Địa chỉ ERC721 certificate contract.
    /// @param owner_ Owner của SavingBank core.
    function initialize(
        address token_,
        address registry_,
        address certificate_,
        address owner_
    ) external initializer {
        if (token_ == address(0) || registry_ == address(0) || certificate_ == address(0) || owner_ == address(0)) {
            revert ZeroAddress();
        }

        __Ownable_init(owner_);
        __Pausable_init();
        __ReentrancyGuard_init();

        token = IERC20(token_);
        registry = IDepositRegistry(registry_);
        certificate = IDepositCertificate(certificate_);

        Vault vault_ = new Vault(address(token), address(this), owner_);
        vault = Vault(vault_);

        feeReceiver = owner_;

        nextPlanId = 1;
    }

    // ---------------------------------------------------------------------
    // Admin: Saving Plans
    // ---------------------------------------------------------------------

    function createPlan(
        uint32 tenorDays,
        uint32 aprBps,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint32 earlyWithdrawPenaltyBps,
        bool enabled
    ) external override onlyOwner {
        if (tenorDays == 0) revert InvalidParameters();
        if (aprBps > MAX_APR_BPS) revert InvalidParameters();
        if (earlyWithdrawPenaltyBps > MAX_PENALTY_BPS) revert InvalidParameters();
        if (maxDeposit != 0 && maxDeposit < minDeposit) revert InvalidParameters();

        uint256 planId = nextPlanId++;

        plans[planId] = SavingPlan({
            id: planId,
            tenorDays: tenorDays,
            aprBps: aprBps,
            minDeposit: minDeposit,
            maxDeposit: maxDeposit,
            earlyWithdrawPenaltyBps: earlyWithdrawPenaltyBps,
            enabled: enabled
        });

        emit PlanCreated(
            planId,
            tenorDays,
            aprBps,
            minDeposit,
            maxDeposit,
            earlyWithdrawPenaltyBps,
            enabled
        );
    }

    function updatePlan(
        uint256 planId,
        uint32 tenorDays,
        uint32 aprBps,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint32 earlyWithdrawPenaltyBps,
        bool enabled
    ) external override onlyOwner {
        SavingPlan storage plan = plans[planId];
        if (plan.id == 0) revert InvalidPlan();

        if (tenorDays == 0) revert InvalidParameters();
        if (aprBps > MAX_APR_BPS) revert InvalidParameters();
        if (earlyWithdrawPenaltyBps > MAX_PENALTY_BPS) revert InvalidParameters();
        if (maxDeposit != 0 && maxDeposit < minDeposit) revert InvalidParameters();

        plan.tenorDays = tenorDays;
        plan.aprBps = aprBps;
        plan.minDeposit = minDeposit;
        plan.maxDeposit = maxDeposit;
        plan.earlyWithdrawPenaltyBps = earlyWithdrawPenaltyBps;
        plan.enabled = enabled;

        emit PlanUpdated(
            planId,
            tenorDays,
            aprBps,
            minDeposit,
            maxDeposit,
            earlyWithdrawPenaltyBps,
            enabled
        );
    }

    // ---------------------------------------------------------------------
    // Admin: Vault & Fee Receiver
    // ---------------------------------------------------------------------

    function setFeeReceiver(address newFeeReceiver) external override onlyOwner {
        feeReceiver = newFeeReceiver;
        emit FeeReceiverUpdated(newFeeReceiver);
    }

    /// @notice Nạp tiền vào vault (chỉ owner)
    function fundVault(uint256 amount) external override onlyOwner {
        if (amount == 0) revert InvalidAmount();
        // Transfer tokens from owner directly into Vault. Vault.fund also
        // attempts a transferFrom, which would cause a duplicate transfer
        // when called from here. Perform a single direct transfer instead.
        token.safeTransferFrom(msg.sender, address(vault), amount);
        emit VaultFunded(msg.sender, amount);
    }

    /// @notice Rút tiền từ vault (chỉ owner)
    function withdrawVault(uint256 amount) external override onlyOwner {
        if (amount == 0) revert InvalidAmount();
        vault.withdrawTo(amount, msg.sender);
        emit VaultWithdrawn(msg.sender, amount);
    }

    function getFeeReceiver() external view override returns (address) {
        return feeReceiver;
    }

    function getVaultBalance() external view override returns (uint256) {
        return vault.getVaultBalance();
    }

    // ---------------------------------------------------------------------
    // Admin: Pause
    // ---------------------------------------------------------------------

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // ---------------------------------------------------------------------
    // User actions
    // ---------------------------------------------------------------------

    function openDeposit(uint256 planId, uint256 amount)
        external
        whenNotPaused
        nonReentrant
        override
        returns (uint256 depositId)
    {
        SavingPlan storage plan = plans[planId];
        if (plan.id == 0) revert InvalidPlan();
        if (!plan.enabled) revert PlanDisabled();
        if (amount == 0) revert InvalidAmount();
        if (amount < plan.minDeposit) revert InvalidAmount();
        if (plan.maxDeposit != 0 && amount > plan.maxDeposit) revert InvalidAmount();

        // 1. Chuyển tiền từ user vào SavingBank (transit ngắn)
        token.safeTransferFrom(msg.sender, address(vault), amount);

        uint64 startAt = uint64(block.timestamp);
        uint64 maturityAt = uint64(block.timestamp + uint64(plan.tenorDays) * 1 days);

        depositId = registry.createDeposit(
            msg.sender,
            planId,
            amount,
            plan.tenorDays,
            plan.aprBps,
            plan.earlyWithdrawPenaltyBps,
            startAt,
            maturityAt
        );

        certificate.mintCertificate(msg.sender, depositId);

        emit DepositOpened(depositId, msg.sender, planId, amount, maturityAt);
    }

    function withdrawAtMaturity(uint256 depositId)
        external
        override
        whenNotPaused
        nonReentrant
    {
        IDepositRegistry.DepositInfo memory dep = registry.deposits(depositId);
        if (dep.id == 0 || dep.owner == address(0)) revert InvalidDeposit();
        if (msg.sender != dep.owner) revert NotDepositOwner();
        if (dep.status != IDepositRegistry.DepositStatus.Active) revert DepositNotActive();
        if (block.timestamp < dep.maturityAt) revert NotMatured();

        uint256 interest = _calculateInterest(dep.principal, dep.aprBps, dep.tenorDays);

        // Vault trả thẳng principal + interest về user (không transit qua SavingBank)
        vault.withdrawTo(dep.principal + interest, msg.sender);

        registry.markWithdrawn(depositId);
        certificate.burnCertificate(depositId);

        emit Withdrawn(depositId, msg.sender, dep.principal, interest, false);
    }

    function earlyWithdraw(uint256 depositId)
        external
        override
        whenNotPaused
        nonReentrant
    {
        IDepositRegistry.DepositInfo memory dep = registry.deposits(depositId);
        if (dep.id == 0 || dep.owner == address(0)) revert InvalidDeposit();
        if (msg.sender != dep.owner) revert NotDepositOwner();
        if (dep.status != IDepositRegistry.DepositStatus.Active) revert DepositNotActive();
        if (block.timestamp >= dep.maturityAt) revert AlreadyMatured();

        uint256 penalty = (dep.principal * dep.earlyWithdrawPenaltyBps) / 10000;
        if (penalty > dep.principal) penalty = dep.principal;

        uint256 userAmount = dep.principal - penalty;

        // Vault trả thẳng userAmount về user
        vault.withdrawTo(userAmount, msg.sender);

        // Phạt (penalty) giữ ở Vault hoặc chuyển feeReceiver
        if (penalty > 0) {
            if (feeReceiver != address(0)) {
                vault.withdrawTo(penalty, feeReceiver);
            }
            // Nếu không có feeReceiver, penalty vẫn ở Vault (tăng vaultBalance)
        }

        registry.markEarlyWithdrawn(depositId);
        certificate.burnCertificate(depositId);

        emit Withdrawn(depositId, msg.sender, dep.principal, 0, true);
    }

    function renewDeposit(uint256 depositId, uint256 newPlanId)
        external
        whenNotPaused
        nonReentrant
        override
        returns (uint256 newDepositId)
    {
        IDepositRegistry.DepositInfo memory dep = registry.deposits(depositId);
        if (dep.id == 0 || dep.owner == address(0)) revert InvalidDeposit();
        if (msg.sender != dep.owner) revert NotDepositOwner();
        if (dep.status != IDepositRegistry.DepositStatus.Active) revert DepositNotActive();
        if (block.timestamp < dep.maturityAt) revert NotMatured();

        SavingPlan storage newPlan = plans[newPlanId];
        if (newPlan.id == 0) revert InvalidPlan();
        if (!newPlan.enabled) revert PlanDisabled();

        uint256 interest = _calculateInterest(dep.principal, dep.aprBps, dep.tenorDays);

        uint256 newPrincipal = dep.principal + interest;

        if (newPrincipal < newPlan.minDeposit) revert InvalidAmount();
        if (newPlan.maxDeposit != 0 && newPrincipal > newPlan.maxDeposit) revert InvalidAmount();

        address owner_ = dep.owner;
        uint64 startAt = uint64(block.timestamp);
        uint64 maturityAt = uint64(block.timestamp + uint64(newPlan.tenorDays) * 1 days);

        registry.markRenewed(depositId);
        certificate.burnCertificate(depositId);

        newDepositId = registry.createRenewedDeposit(
            owner_,
            newPlanId,
            newPrincipal,
            newPlan.tenorDays,
            newPlan.aprBps,
            newPlan.earlyWithdrawPenaltyBps,
            startAt,
            maturityAt
        );

        certificate.mintCertificate(owner_, newDepositId);

        emit Renewed(depositId, newDepositId, newPrincipal);
    }

    /// @notice Get all active deposit IDs of the caller (empty array if none).
    function getMyActiveDepositId() external view override returns (uint256[] memory) {
        return registry.getActiveDepositId(msg.sender);
    }

    /// @notice Get all active deposit IDs of a user (empty array if none).
    function getActiveDepositId(address user) external view override returns (uint256[] memory) {
        return registry.getActiveDepositId(user);
    }

    function _calculateInterest(
        uint256 principal,
        uint32 aprBps,
        uint32 tenorDays
    ) internal pure returns (uint256) {
        if (principal == 0 || aprBps == 0 || tenorDays == 0) return 0;
        uint256 tenorSeconds = uint256(tenorDays) * 1 days;
        uint256 yearInSeconds = 365 days;
        return (principal * aprBps * tenorSeconds) / (yearInSeconds * 10000);
    }
}","keccak256":"0xfcffbc4bbbf0569e960a00efd527a1ef410d59fc55fd6256e13a43defdaf2c5f","license":"MIT"},"contracts/SavingBankUpgradeableFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {SavingBankUpgradeable} from "./SavingBankUpgradeable.sol";
import {DepositRegistry} from "./DepositRegistry.sol";
import {DepositCertificateUpgradeable} from "./DepositCertificateUpgradeable.sol";

interface IOwnableLike {
    function owner() external view returns (address);
}

contract SavingBankUpgradeableFactory is Ownable {
    mapping(address => address[]) public userBanks;

    event SavingBankCreated(
        address indexed creator,
        address indexed bank,
        address indexed registry,
        address certificate,
        address token,
        string name,
        string symbol
    );

    constructor() Ownable(msg.sender) {}

    function createSavingBank(
        IERC20 token,
        string memory name_,
        string memory symbol_
    ) external returns (address bank, address registry, address certificate) {
        require(address(token) != address(0), "TOKEN_ZERO");

        DepositRegistry reg = new DepositRegistry();

        DepositCertificateUpgradeable cert = new DepositCertificateUpgradeable();
        cert.initialize(name_, symbol_, address(this));

        SavingBankUpgradeable savingBank = new SavingBankUpgradeable();
        savingBank.initialize(address(token), address(reg), address(cert), msg.sender);

        reg.setBankExecutable(address(savingBank), true);
        cert.setBankExecutable(address(savingBank));

        cert.transferOwnership(msg.sender);
        reg.transferOwnership(msg.sender);

        bank = address(savingBank);
        registry = address(reg);
        certificate = address(cert);

        userBanks[msg.sender].push(bank);

        emit SavingBankCreated(
            msg.sender,
            bank,
            registry,
            certificate,
            address(token),
            name_,
            symbol_
        );
    }

    function createSavingBankWithExistingState(
        IERC20 token,
        address registry,
        address certificate
    ) external returns (address bank) {
        require(address(token) != address(0), "TOKEN_ZERO");
        require(registry != address(0), "REG_ZERO");
        require(certificate != address(0), "CERT_ZERO");

        require(IOwnableLike(registry).owner() == msg.sender, "NOT_REG_OWNER");
        require(IOwnableLike(certificate).owner() == msg.sender, "NOT_CERT_OWNER");

        SavingBankUpgradeable savingBank = new SavingBankUpgradeable();
        savingBank.initialize(address(token), registry, certificate, msg.sender);

        bank = address(savingBank);

        userBanks[msg.sender].push(bank);

        string memory name_ = DepositCertificateUpgradeable(certificate).name();
        string memory symbol_ = DepositCertificateUpgradeable(certificate).symbol();

        emit SavingBankCreated(
            msg.sender,
            bank,
            registry,
            certificate,
            address(token),
            name_,
            symbol_
        );
    }

    function getUserBanks(address user) external view returns (address[] memory) {
        return userBanks[user];
    }
}","keccak256":"0x5fbdab9e1b514716051cedf4759ad478ad8544825dfdfa3a59bc994bb60416f1","license":"MIT"},"contracts/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/// @title Vault
/// @notice Contract riêng quản lý toàn bộ tiền gốc (principal) và lãi (interest) của hệ thống tiết kiệm.
/// Chỉ owner (admin) và SavingBank core được phép nạp/rút tiền.
/// Tiền không được giữ ở SavingBank, mà chuyển thẳng vào Vault.
contract Vault is Ownable {
    using SafeERC20 for IERC20;

    IERC20 public immutable token;
    address public immutable savingBank;


    event VaultFunded(address indexed from, uint256 amount);
    event VaultWithdrawn(address indexed to, uint256 amount);
    event PenaltyReceived(address indexed from, uint256 amount);

    error OnlySavingBankOrOwner();
    error InsufficientVaultBalance();
    error InvalidAmount();
    error ZeroAddress();

    modifier onlySavingBankOrOwner() {
        if (msg.sender != savingBank && msg.sender != owner()) {
            revert OnlySavingBankOrOwner();
        }
        _;
    }

    constructor(
        address _token,
        address _savingBank,
        address _initialOwner
    ) Ownable(_initialOwner) {
        if (_token == address(0) || _savingBank == address(0) || _initialOwner == address(0)) {
            revert ZeroAddress();
        }
        token = IERC20(_token);
        savingBank = _savingBank;
    }

    /// @notice Nạp tiền vào vault (principal hoặc interest từ SavingBank)
    /// @param amount Số lượng token cần nạp
    function fund(uint256 amount) external onlySavingBankOrOwner {
        if (amount == 0) revert InvalidAmount();

        token.safeTransferFrom(msg.sender, address(this), amount);

        emit VaultFunded(msg.sender, amount);
    }

    /// @notice Rút tiền từ vault về một địa chỉ bất kỳ (user hoặc feeReceiver)
    /// @param amount Số lượng token cần rút
    /// @param to Địa chỉ nhận tiền
    function withdrawTo(uint256 amount, address to) external onlySavingBankOrOwner {
        if (amount == 0) revert InvalidAmount();
        uint256 vaultBalance = token.balanceOf(address(this));
        if (amount > vaultBalance) revert InsufficientVaultBalance();
        if (to == address(0)) revert ZeroAddress();

        token.safeTransfer(to, amount);

        emit VaultWithdrawn(to, amount);
    }

    /// @notice Rút tiền về chính caller (dùng khi SavingBank cần interest để tính toán renew)
    /// @param amount Số lượng token cần rút
    function withdraw(uint256 amount) external onlySavingBankOrOwner {
        if (amount == 0) revert InvalidAmount();
        uint256 vaultBalance = token.balanceOf(address(this));
        if (amount > vaultBalance) revert InsufficientVaultBalance();
        if (msg.sender == address(0)) revert ZeroAddress();

        token.safeTransfer(msg.sender, amount);

        emit VaultWithdrawn(msg.sender, amount);
    }

    /// @notice Nhận penalty từ earlyWithdraw (từ SavingBank hoặc feeReceiver)
    /// @dev Nếu feeReceiver là address(0), penalty sẽ được giữ lại trong vault
    function receivePenalty(uint256 amount) external onlySavingBankOrOwner {
        if (amount == 0) return; // Không revert để tránh block tx

        token.safeTransferFrom(msg.sender, address(this), amount);

        emit PenaltyReceived(msg.sender, amount);
    }

    /// @notice Xem số dư hiện tại trong vault
    function getVaultBalance() external view returns (uint256) {
        return token.balanceOf(address(this));
    }

    /// @notice Xem token address
    function getToken() external view returns (address) {
        return address(token);
    }

    /// @notice Xem địa chỉ SavingBank core
    function getSavingBank() external view returns (address) {
        return savingBank;
    }
}","keccak256":"0xa5d246a30df05455fac56ed1f11c7204bad7385495c3b8c4bbb017960c5e8aab","license":"MIT"},"contracts/interfaces/IDepositCertificate.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/// @title IDepositCertificate
/// @notice Interface tối giản cho contract ERC721 đại diện sổ tiết kiệm.
interface IDepositCertificate {
    /// @notice Mint NFT certificate mới cho 1 deposit.
    /// @param to Địa chỉ nhận NFT.
    /// @param depositId Mã sổ tiết kiệm (sử dụng luôn làm tokenId).
    function mintCertificate(address to, uint256 depositId) external;

    /// @notice Burn NFT certificate khi sổ kết thúc hoặc gia hạn.
    /// @param depositId Mã sổ / tokenId cần burn.
    function burnCertificate(uint256 depositId) external;

    /// @notice Lấy owner hiện tại của 1 certificate.
    /// @param tokenId Token ID (bằng depositId).
    function ownerDepositCertificateOf(uint256 tokenId) external view returns (address);
}
","keccak256":"0x92d1420e07b43a8330102b1fd210c230a286319c17a56547f97ebdef27222a72","license":"MIT"},"contracts/interfaces/IDepositManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/// @title IDepositManager
/// @notice Interface cho các nghiệp vụ mở/tất toán/gia hạn sổ tiết kiệm.
interface IDepositManager {
    event DepositOpened(
        uint256 indexed depositId,
        address indexed owner,
        uint256 indexed planId,
        uint256 principal,
        uint64 maturityAt
    );

    event Withdrawn(
        uint256 indexed depositId,
        address indexed owner,
        uint256 principal,
        uint256 interest,
        bool isEarly
    );

    event Renewed(
        uint256 indexed oldDepositId,
        uint256 indexed newDepositId,
        uint256 newPrincipal
    );

    function openDeposit(uint256 planId, uint256 amount)
        external
        returns (uint256 depositId);

    function withdrawAtMaturity(uint256 depositId) external;

    function earlyWithdraw(uint256 depositId) external;

    function renewDeposit(uint256 depositId, uint256 newPlanId)
        external
        returns (uint256 newDepositId);

    function getMyActiveDepositId() external view returns (uint256[] memory);

    function getActiveDepositId(address user) external view returns (uint256[] memory);
}
","keccak256":"0x633104dd11ac727ff3694397baafc2df8f429de274310223fb8a004503ff68a8","license":"MIT"},"contracts/interfaces/IDepositRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/// @title IDepositRegistry
/// @notice Interface cho contract lưu trữ toàn bộ state Deposit, tách biệt khỏi SavingBank core.
interface IDepositRegistry {
    enum DepositStatus {
        Active,
        Withdrawn,
        EarlyWithdrawn,
        Renewed,
        Cancelled
    }

    struct DepositInfo {
        uint256 id;
        uint256 planId;
        address owner;
        uint256 principal;
        uint32 tenorDays;
        uint32 aprBps;
        uint32 earlyWithdrawPenaltyBps;
        uint64 startAt;
        uint64 maturityAt;
        DepositStatus status;
    }

    function nextDepositId() external view returns (uint256);

    function deposits(uint256 depositId) external view returns (DepositInfo memory);

    function activeDepositOf(address user) external view returns (uint256[] memory);

    function getActiveDepositId(address user) external view returns (uint256[] memory);

    function createDeposit(
        address owner,
        uint256 planId,
        uint256 principal,
        uint32 tenorDays,
        uint32 aprBps,
        uint32 earlyWithdrawPenaltyBps,
        uint64 startAt,
        uint64 maturityAt
    ) external returns (uint256 depositId);

    function markWithdrawn(uint256 depositId) external;

    function markEarlyWithdrawn(uint256 depositId) external;

    function markRenewed(uint256 depositId) external;

    function createRenewedDeposit(
        address owner,
        uint256 newPlanId,
        uint256 newPrincipal,
        uint32 tenorDays,
        uint32 aprBps,
        uint32 earlyWithdrawPenaltyBps,
        uint64 startAt,
        uint64 maturityAt
    ) external returns (uint256 newDepositId);
}
