     * @return Total amount paid
     */
    function getTotalStudentPayments(address student) external view returns (uint256);

    /**
     * @dev Get total payments received by merchant
     * @param merchant Merchant address
     * @return Total amount received
     */
    function getTotalMerchantRevenue(address merchant) external view returns (uint256);

    /**
     * @dev Get wallet ID linked to payment method
     * @param student Student address
     * @param paymentMethod Payment method name
     * @return Linked wallet ID
     */
    function getLinkedWallet(address student, string calldata paymentMethod) external view returns (string memory);

    /**
     * @dev Get total transaction count
     * @return Total number of payment transactions
     */
    function getTotalPaymentCount() external view returns (uint256);
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IMerchantRegistry} from "./interfaces/IMerchantRegistry.sol";

/**
 * @title MerchantRegistry
 * @dev Registry for managing merchant registrations, approvals, and information
 * Handles merchant onboarding, verification, and commission management
 */
contract MerchantRegistry is IMerchantRegistry, Ownable {
    
    // ============ State Variables ============
    mapping(address => Merchant) private merchants;
    mapping(address => bool) private registeredMerchants;
    address[] private merchantList;
    mapping(string => address[]) private merchantsByCategory;
    mapping(address => bool) private approvedMerchants;
    
    // Default commission rate (in basis points, 250 = 2.5%)
    uint256 private defaultCommissionRate = 250;

    // ============ Constructor ============
    constructor() Ownable(msg.sender) {}

    // ============ Modifiers ============
    modifier onlyRegistered(address merchant) {
        require(registeredMerchants[merchant], "Merchant not registered");
        _;
    }

    modifier onlyApproved(address merchant) {
        require(approvedMerchants[merchant], "Merchant not approved");
        _;
    }

    // ============ Merchant Registration ============
    /**
     * @dev Register a new merchant
     */
    function registerMerchant(
        string calldata name,
        string calldata category,
        string calldata contactEmail,
        string calldata contactPhone
    ) external returns (address merchantAddress) {
        require(!registeredMerchants[msg.sender], "Already registered");
        require(bytes(name).length > 0, "Invalid name");
        require(bytes(category).length > 0, "Invalid category");

        merchantAddress = msg.sender;
        
        merchants[merchantAddress] = Merchant({
            merchantAddress: merchantAddress,
            name: name,
            category: category,
            contactEmail: contactEmail,
            contactPhone: contactPhone,
            isApproved: false,
            isActive: true,
            commissionRate: defaultCommissionRate,
            totalTransactions: 0,
            totalRevenue: 0,
            registrationDate: block.timestamp,
            approvalDate: 0
        });

        registeredMerchants[merchantAddress] = true;
        merchantList.push(merchantAddress);
        merchantsByCategory[category].push(merchantAddress);

        emit MerchantRegistered(
            merchantAddress,
            name,
            category,
            block.timestamp
        );
    }

    /**
     * @dev Update merchant information
     */
    function updateMerchant(
        address merchant,
        string calldata name,
        string calldata category,
        string calldata contactEmail,
        string calldata contactPhone
    ) external onlyRegistered(merchant) {
        require(msg.sender == merchant || msg.sender == owner(), "Unauthorized");
        require(bytes(name).length > 0, "Invalid name");
        require(bytes(category).length > 0, "Invalid category");

        Merchant storage merch = merchants[merchant];
        merch.name = name;
        merch.category = category;
        merch.contactEmail = contactEmail;
        merch.contactPhone = contactPhone;

        emit MerchantUpdated(
            merchant,
            name,
            category,
            block.timestamp
        );
    }

    /**
     * @dev Approve a merchant registration
     */
    function approveMerchant(address merchant) external onlyOwner onlyRegistered(merchant) {
        require(!approvedMerchants[merchant], "Already approved");

        approvedMerchants[merchant] = true;
        merchants[merchant].isApproved = true;
        merchants[merchant].approvalDate = block.timestamp;

        emit MerchantApproved(merchant, block.timestamp);
    }

    /**
     * @dev Reject a merchant registration
     */
    function rejectMerchant(address merchant, string calldata reason) external onlyOwner onlyRegistered(merchant) {
        require(!approvedMerchants[merchant], "Already approved");

        registeredMerchants[merchant] = false;
        approvedMerchants[merchant] = false;

        emit MerchantRejected(merchant, reason, block.timestamp);
    }

    /**
     * @dev Deactivate a merchant account
     */
    function deactivateMerchant(address merchant) external onlyOwner onlyRegistered(merchant) {
        require(merchants[merchant].isActive, "Already inactive");

        merchants[merchant].isActive = false;

        emit MerchantDeactivated(merchant, block.timestamp);
    }

    /**
     * @dev Reactivate a merchant account
     */
    function reactivateMerchant(address merchant) external onlyOwner onlyRegistered(merchant) {
        require(!merchants[merchant].isActive, "Already active");

        merchants[merchant].isActive = true;

        emit MerchantUpdated(
            merchant,
            merchants[merchant].name,
            merchants[merchant].category,
            block.timestamp
        );
    }

    /**
     * @dev Set commission rate for a merchant
     */
    function setCommissionRate(address merchant, uint256 commissionRate) external onlyOwner onlyRegistered(merchant) {
        require(commissionRate <= 10000, "Commission rate too high"); // Max 100%

        merchants[merchant].commissionRate = commissionRate;

        emit CommissionRateSet(merchant, commissionRate, block.timestamp);
    }

    /**
     * @dev Update merchant transaction stats
     */
    function updateTransactionStats(address merchant, uint256 transactionAmount) external {
        require(registeredMerchants[merchant], "Merchant not registered");
        
        Merchant storage merch = merchants[merchant];
        merch.totalTransactions += 1;
        merch.totalRevenue += transactionAmount;
    }

    // ============ Query Functions ============
    /**
     * @dev Get merchant information
     */
    function getMerchant(address merchant) external view returns (Merchant memory) {
        require(registeredMerchants[merchant], "Merchant not registered");
        return merchants[merchant];
    }

    /**
     * @dev Check if address is an approved merchant
     */
    function isApprovedMerchant(address merchant) external view returns (bool) {
        return approvedMerchants[merchant] && merchants[merchant].isActive;
    }

    /**
     * @dev Check if merchant is active
     */
    function isMerchantActive(address merchant) external view returns (bool) {
        return registeredMerchants[merchant] && merchants[merchant].isActive;
    }

    /**
     * @dev Get commission rate for merchant
     */
    function getCommissionRate(address merchant) external view returns (uint256) {
        require(registeredMerchants[merchant], "Merchant not registered");
        return merchants[merchant].commissionRate;
    }

    /**
     * @dev Get all merchants in a category
     */
    function getMerchantsByCategory(string calldata category) external view returns (address[] memory) {
        return merchantsByCategory[category];
    }

    /**
     * @dev Get total registered merchants
     */
    function getTotalMerchants() external view returns (uint256) {
        return merchantList.length;
    }

    /**
     * @dev Get total approved merchants
     */
    function getTotalApprovedMerchants() external view returns (uint256) {
        uint256 count = 0;
        for (uint256 i = 0; i < merchantList.length; i++) {
            if (approvedMerchants[merchantList[i]]) {
                count++;
            }
        }
        return count;
    }

    /**
     * @dev Get merchant by index
     */
    function getMerchantByIndex(uint256 index) external view returns (address) {
        require(index < merchantList.length, "Index out of bounds");
        return merchantList[index];
    }

    /**
     * @dev Check if address is registered merchant
     */
    function isMerchantRegistered(address merchant) external view returns (bool) {
        return registeredMerchants[merchant];
    }

    /**
     * @dev Set default commission rate
     */
    function setDefaultCommissionRate(uint256 rate) external onlyOwner {
        require(rate <= 10000, "Commission rate too high");
        defaultCommissionRate = rate;
    }

    /**
     * @dev Get default commission rate
     */
    function getDefaultCommissionRate() external view returns (uint256) {
        return defaultCommissionRate;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IPaymentProcessor} from "./interfaces/IPaymentProcessor.sol";

/**
 * @title PaymentProcessor
 * @dev Payment processing system supporting multiple payment methods
 * Allows students to make payments to merchants using VNDC or other tokens
 */
contract PaymentProcessor is IPaymentProcessor, Ownable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============
    uint256 private paymentIdCounter = 1;
    
    mapping(string => PaymentMethod) private paymentMethods;
    mapping(string => bool) private supportedMethods;
    mapping(uint256 => PaymentRecord) private paymentRecords;
    mapping(address => uint256[]) private studentPayments;
    mapping(address => uint256[]) private merchantPayments;
    mapping(address => mapping(string => string)) private linkedWallets;
    
    string[] private paymentMethodsList;

    // ============ Modifiers ============
    modifier validPaymentMethod(string calldata paymentMethod) {
        require(supportedMethods[paymentMethod], "Payment method not supported");
        require(paymentMethods[paymentMethod].isActive, "Payment method is inactive");
        _;
    }

    // ============ Constructor ============
    constructor() Ownable(msg.sender) {
        // Initialize with VNDC as default payment method
        _addPaymentMethod("VNDC", address(0), 0, type(uint256).max);
    }

    // ============ Payment Processing ============
    /**
     * @dev Process a payment from student to merchant
     */
    function processPayment(
        address merchant,
        uint256 amount,
        string calldata paymentMethod
    ) external validPaymentMethod(paymentMethod) returns (uint256 paymentId) {
        require(merchant != address(0), "Invalid merchant address");
        require(amount > 0, "Amount must be greater than 0");
        require(msg.sender != merchant, "Cannot pay to self");

        PaymentMethod storage method = paymentMethods[paymentMethod];
        require(amount >= method.minAmount, "Amount below minimum");
        require(amount <= method.maxAmount, "Amount exceeds maximum");

        // Process token transfer if applicable
        if (method.tokenAddress != address(0)) {
            IERC20(method.tokenAddress).safeTransferFrom(msg.sender, merchant, amount);
        }

        paymentId = paymentIdCounter++;
        
        PaymentRecord storage record = paymentRecords[paymentId];
        record.paymentId = paymentId;
        record.student = msg.sender;
        record.merchant = merchant;
        record.amount = amount;
        record.paymentMethod = paymentMethod;
        record.timestamp = block.timestamp;
        record.isRefunded = false;

        studentPayments[msg.sender].push(paymentId);
        merchantPayments[merchant].push(paymentId);

        emit PaymentProcessed(
            paymentId,
            msg.sender,
            merchant,
            amount,
            paymentMethod,
            block.timestamp
        );
    }

    /**
     * @dev Refund a payment
     */
    function refundPayment(
        uint256 paymentId,
        string calldata reason
    ) external {
        PaymentRecord storage record = paymentRecords[paymentId];
        
        require(record.paymentId != 0, "Payment not found");
        require(!record.isRefunded, "Payment already refunded");
        require(
            msg.sender == record.student || msg.sender == owner(),
            "Unauthorized refund"
        );

        PaymentMethod storage method = paymentMethods[record.paymentMethod];
        require(method.isActive, "Payment method is inactive");

        // Process refund
        if (method.tokenAddress != address(0)) {
            IERC20(method.tokenAddress).safeTransferFrom(
                record.merchant,
                record.student,
                record.amount
            );
        }

        record.isRefunded = true;
        record.refundAmount = record.amount;
        record.refundReason = reason;
        record.refundTimestamp = block.timestamp;

        emit PaymentRefunded(
            paymentId,
            record.student,
            record.amount,
            reason,
            block.timestamp
        );
    }

    /**
     * @dev Link a wallet to a payment method
     */
    function linkWallet(
        string calldata paymentMethod,
        string calldata walletId
    ) external validPaymentMethod(paymentMethod) {
        require(bytes(walletId).length > 0, "Invalid wallet ID");
        
        linkedWallets[msg.sender][paymentMethod] = walletId;

        emit WalletLinked(msg.sender, paymentMethod, walletId);
    }

    /**
     * @dev Unlink a wallet from a payment method
     */
    function unlinkWallet(string calldata paymentMethod) external {
        require(bytes(linkedWallets[msg.sender][paymentMethod]).length > 0, "No linked wallet");
        
        delete linkedWallets[msg.sender][paymentMethod];

        emit WalletUnlinked(msg.sender, paymentMethod);
    }

    // ============ Admin Functions ============
    /**
     * @dev Add a new payment method
     */
    function addPaymentMethod(
        string calldata name,
        address tokenAddress,
        uint256 minAmount,
        uint256 maxAmount
    ) external onlyOwner {
        _addPaymentMethod(name, tokenAddress, minAmount, maxAmount);
    }

    /**
     * @dev Internal helper to add payment method
     */
    function _addPaymentMethod(
        string memory name,
        address tokenAddress,
        uint256 minAmount,
        uint256 maxAmount
    ) internal {
        require(bytes(name).length > 0, "Invalid payment method name");
        require(minAmount <= maxAmount, "Invalid amount range");
        require(!supportedMethods[name], "Payment method already exists");

        paymentMethods[name] = PaymentMethod({
            name: name,
            tokenAddress: tokenAddress,
            isActive: true,
            minAmount: minAmount,
            maxAmount: maxAmount
        });

        supportedMethods[name] = true;
        paymentMethodsList.push(name);

        emit PaymentMethodAdded(name, tokenAddress);
    }

    /**
     * @dev Remove a payment method
     */
    function removePaymentMethod(string calldata paymentMethod) external onlyOwner {
        require(supportedMethods[paymentMethod], "Payment method not found");
        require(
            keccak256(bytes(paymentMethod)) != keccak256(bytes("VNDC")),
            "Cannot remove VNDC"
        );

        paymentMethods[paymentMethod].isActive = false;
        supportedMethods[paymentMethod] = false;

        emit PaymentMethodRemoved(paymentMethod);
    }

    // ============ Query Functions ============
    /**
     * @dev Get payment record by ID
     */
    function getPaymentRecord(uint256 paymentId) external view returns (PaymentRecord memory) {
        require(paymentRecords[paymentId].paymentId != 0, "Payment not found");
        return paymentRecords[paymentId];
    }

    /**
     * @dev Get all payments for a student
     */
    function getStudentPayments(address student) external view returns (PaymentRecord[] memory) {
        uint256[] memory ids = studentPayments[student];
        PaymentRecord[] memory records = new PaymentRecord[](ids.length);
        
        for (uint256 i = 0; i < ids.length; i++) {
            records[i] = paymentRecords[ids[i]];
        }
        
        return records;
    }

    /**
     * @dev Get all payments for a merchant
     */
    function getMerchantPayments(address merchant) external view returns (PaymentRecord[] memory) {
        uint256[] memory ids = merchantPayments[merchant];
        PaymentRecord[] memory records = new PaymentRecord[](ids.length);
        
        for (uint256 i = 0; i < ids.length; i++) {
            records[i] = paymentRecords[ids[i]];
        }
        
        return records;
    }

    /**
     * @dev Check if a payment method is supported
     */
    function isPaymentMethodSupported(string calldata paymentMethod) external view returns (bool) {
        return supportedMethods[paymentMethod] && paymentMethods[paymentMethod].isActive;
    }

    /**
     * @dev Get payment method details
     */
    function getPaymentMethod(string calldata paymentMethod) external view returns (PaymentMethod memory) {
        require(supportedMethods[paymentMethod], "Payment method not found");
        return paymentMethods[paymentMethod];
    }

    /**
     * @dev Get total payments made by student
     */
    function getTotalStudentPayments(address student) external view returns (uint256) {
        uint256 total = 0;
        uint256[] memory ids = studentPayments[student];
        
        for (uint256 i = 0; i < ids.length; i++) {
            if (!paymentRecords[ids[i]].isRefunded) {
                total += paymentRecords[ids[i]].amount;
            }
        }
        
        return total;
    }

    /**
     * @dev Get total payments received by merchant
     */
    function getTotalMerchantRevenue(address merchant) external view returns (uint256) {
        uint256 total = 0;
        uint256[] memory ids = merchantPayments[merchant];
        
        for (uint256 i = 0; i < ids.length; i++) {
            if (!paymentRecords[ids[i]].isRefunded) {
                total += paymentRecords[ids[i]].amount;
            }
        }
        
        return total;
    }

    /**
     * @dev Get wallet ID linked to payment method
     */
    function getLinkedWallet(address student, string calldata paymentMethod) external view returns (string memory) {
        return linkedWallets[student][paymentMethod];
    }

    /**
     * @dev Get total transaction count
     */
    function getTotalPaymentCount() external view returns (uint256) {
        return paymentIdCounter - 1;
    }
}
