// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./IVNDCCore.sol";

/// @title VNDC Token
/// @notice Vietnam Digital Currency - ERC20 token for VNDC ecosystem
/// @dev Implements ERC20 with permit extension (ERC2612) for gas optimization
contract VNDC is
    ERC20,
    ERC20Permit,
    ERC20Burnable,
    Ownable,
    Pausable,
    IVNDCToken,
    IVNDCEvents
{
    // Minting and burning rights
    mapping(address => bool) private _minters;
    mapping(address => bool) private _burners;

    // Events
    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);
    event BurnerAdded(address indexed account);
    event BurnerRemoved(address indexed account);

    /// @notice Initialize VNDC Token
    /// @param initialSupply Initial token supply (in wei, 18 decimals)
    constructor(
        uint256 initialSupply
    )
        ERC20("Vietnam Digital Currency", "VNDC")
        ERC20Permit("Vietnam Digital Currency")
        Ownable(msg.sender)
    {
        // Give owner minting rights and burner rights
        _minters[msg.sender] = true;
        _burners[msg.sender] = true;

        // Mint initial supply to owner
        if (initialSupply > 0) {
            _mint(msg.sender, initialSupply);
            emit TokenMinted(msg.sender, initialSupply);
        }
    }

    // ================== Minting & Burning ==================

    /// @notice Mint new tokens
    /// @param to Recipient address
    /// @param amount Amount to mint
    function mint(address to, uint256 amount) external {
        require(_minters[msg.sender], "VNDC: Caller is not a minter");
        require(to != address(0), "VNDC: Cannot mint to zero address");
        require(amount > 0, "VNDC: Amount must be greater than 0");

        _mint(to, amount);
        emit TokenMinted(to, amount);
    }

    /// @notice Burn tokens from caller
    /// @param amount Amount to burn
    function burn(uint256 amount) public override(ERC20Burnable) {
        require(amount > 0, "VNDC: Amount must be greater than 0");
        _burn(msg.sender, amount);
        emit TokenBurned(msg.sender, amount);
    }

    /// @notice Burn tokens from another address (if authorized)
    /// @param from Account to burn from
    /// @param amount Amount to burn
    function burnFrom(address from, uint256 amount)
        public
        override(ERC20Burnable, IVNDCToken)
    {
        require(_burners[msg.sender], "VNDC: Caller is not a burner");
        uint256 currentAllowance = allowance(from, msg.sender);
        require(
            currentAllowance >= amount,
            "ERC20: insufficient allowance"
        );
        unchecked {
            _approve(from, msg.sender, currentAllowance - amount);
        }
        _burn(from, amount);
        emit TokenBurned(from, amount);
    }

    // ================== Minter Management ==================

    /// @notice Add minter role
    /// @param account Address to add minting rights
    function addMinter(address account) external onlyOwner {
        require(account != address(0), "VNDC: Invalid address");
        _minters[account] = true;
        emit MinterAdded(account);
    }

    /// @notice Remove minter role
    /// @param account Address to remove minting rights
    function removeMinter(address account) external onlyOwner {
        _minters[account] = false;
        emit MinterRemoved(account);
    }

    /// @notice Check if address is minter
    /// @param account Address to check
    /// @return True if address has minting rights
    function isMinter(address account) external view returns (bool) {
        return _minters[account];
    }

    // ================== Burner Management ==================

    /// @notice Add burner role
    /// @param account Address to add burning rights
    function addBurner(address account) external onlyOwner {
        require(account != address(0), "VNDC: Invalid address");
        _burners[account] = true;
        emit BurnerAdded(account);
    }

    /// @notice Remove burner role
    /// @param account Address to remove burning rights
    function removeBurner(address account) external onlyOwner {
        _burners[account] = false;
        emit BurnerRemoved(account);
    }

    /// @notice Check if address is burner
    /// @param account Address to check
    /// @return True if address has burning rights
    function isBurner(address account) external view returns (bool) {
        return _burners[account];
    }

    // ================== Pausable Functions ==================

    /// @notice Pause all transfers
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Unpause all transfers
    function unpause() external onlyOwner {
        _unpause();
    }

    /// @notice Check if contract is paused
    /// @return True if paused
    function isPaused() external view returns (bool) {
        return paused();
    }

    // ================== Override Transfer Functions ==================

    /// @notice Override transfer to check pause status
    function transfer(address to, uint256 amount)
        public
        override(ERC20)
        whenNotPaused
        returns (bool)
    {
        return super.transfer(to, amount);
    }

    /// @notice Override transferFrom to check pause status
    function transferFrom(
        address from,
        address to,
        uint256 amount
    )
        public
        override(ERC20)
        whenNotPaused
        returns (bool)
    {
        return super.transferFrom(from, to, amount);
    }

    /// @notice Override _update to apply pause check
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._update(from, to, amount);
    }

    // ================== Metadata ==================

    /// @notice Get decimals
    /// @return Decimal places (18)
    function decimals() public view override(ERC20) returns (uint8) {
        return 18;
    }

    /// @notice Get token name
    /// @return Token name
    function name() public view override(ERC20) returns (string memory) {
        return super.name();
    }

    /// @notice Get token symbol
    /// @return Token symbol
    function symbol() public view override(ERC20) returns (string memory) {
        return super.symbol();
    }

    // ================== Permit (ERC2612) ==================

    /// @notice Permit function for gas-free approvals
    /// @param owner Token owner
    /// @param spender Spender address
    /// @param amount Amount to approve
    /// @param deadline Deadline for permit (unix timestamp)
    /// @param v Signature v
    /// @param r Signature r
    /// @param s Signature s
    function permit(
        address owner,
        address spender,
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public override(ERC20Permit, IVNDCToken) {
        super.permit(owner, spender, amount, deadline, v, r, s);
    }

    // ================== Utility ==================

    /// @notice Get token info
    /// @return name Token name
    /// @return symbol Token symbol
    /// @return decimals Token decimals
    /// @return totalSupply Total token supply
    function getTokenInfo()
        external
        view
        returns (
            string memory,
            string memory,
            uint8,
            uint256
        )
    {
        return (name(), symbol(), decimals(), totalSupply());
    }
}

contracts/modules/002-credentials/Creden.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {ERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import {ERC721URIStorage} from "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import {ERC721Burnable} from "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import "./ICredentials.sol";

/// @title CredentialNFT
/// @notice ERC-721 NFT for representing educational credentials
/// @dev Extends ERC721 with metadata URI storage and burning capabilities
contract CredentialNFT is
    ERC721,
    ERC721URIStorage,
    ERC721Burnable,
    Ownable
{
    uint256 private _tokenIdCounter;
    mapping(address => uint256[]) private _userTokens;
    mapping(uint256 => bool) private _tokenExists;

    event TokenMinted(uint256 indexed tokenId, address indexed to, string uri);
    event TokenBurned(uint256 indexed tokenId);

    constructor() ERC721("VNDC Credentials", "VNDC-CRED") Ownable(msg.sender) {}

    /// @notice Mint new credential NFT
    /// @param to Recipient address
    /// @param credentialURI IPFS/metadata URI
    /// @return tokenId The minted token ID
    function mint(address to, string calldata credentialURI)
        external
        onlyOwner
        returns (uint256)
    {
        require(to != address(0), "CredentialNFT: Cannot mint to zero address");
        require(bytes(credentialURI).length > 0, "CredentialNFT: URI cannot be empty");

        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter++;

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, credentialURI);
        _tokenExists[tokenId] = true;
        _userTokens[to].push(tokenId);

        emit TokenMinted(tokenId, to, credentialURI);
        return tokenId;
    }

    /// @notice Burn credential NFT
    /// @param tokenId Token ID to burn
    function burn(uint256 tokenId) public override(ERC721Burnable) {
        require(_tokenExists[tokenId], "CredentialNFT: Token does not exist");
        
        address owner = ownerOf(tokenId);
        _tokenExists[tokenId] = false;
        
        // Remove from user tokens array
        uint256[] storage tokens = _userTokens[owner];
        for (uint256 i = 0; i < tokens.length; i++) {
            if (tokens[i] == tokenId) {
                tokens[i] = tokens[tokens.length - 1];
                tokens.pop();
                break;
            }
        }

        super.burn(tokenId);
        emit TokenBurned(tokenId);
    }

    /// @notice Get all tokens owned by address
    /// @param owner Owner address
    /// @return Array of token IDs
    function tokensOfOwner(address owner) external view returns (uint256[] memory) {
        require(owner != address(0), "CredentialNFT: Invalid address");
        return _userTokens[owner];
    }

    /// @notice Check if token exists
    /// @param tokenId Token ID
    /// @return True if token exists
    function exists(uint256 tokenId) external view  returns (bool) {
        return _tokenExists[tokenId];
    }

    // ============ Override Compatibility Functions ============

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value) internal override(ERC721) {
        super._increaseBalance(account, value);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import "./ICredentials.sol";
import "./CredentialNFT.sol";

/// @title CredentialVerification
/// @notice Manages credential issuance, verification, and revocation
/// @dev Uses CredentialNFT for NFT minting and storage
contract CredentialVerification is Ownable, ICredentialVerification, ICredentialEvents {
    CredentialNFT public nftContract;

    mapping(uint256 => Credential) private _credentials;
    mapping(address => uint256[]) private _userCredentials;
    mapping(address => bool) private _issuers;

    // Events from interface
    event IssuerAdded(address indexed issuer);
    event IssuerRemoved(address indexed issuer);

    constructor(address nftContractAddress) Ownable(msg.sender) {
        require(nftContractAddress != address(0), "CredentialVerification: Invalid NFT contract");
        nftContract = CredentialNFT(nftContractAddress);
        _issuers[msg.sender] = true;
    }

    // ============ Issuer Management ============

    /// @notice Add issuer (teacher/admin)
    /// @param issuer Address to grant issuer role
    function addIssuer(address issuer) external onlyOwner {
        require(issuer != address(0), "CredentialVerification: Invalid issuer");
        require(!_issuers[issuer], "CredentialVerification: Already an issuer");
        _issuers[issuer] = true;
        emit IssuerAdded(issuer);
    }

    /// @notice Remove issuer
    /// @param issuer Address to revoke issuer role
    function removeIssuer(address issuer) external onlyOwner {
        require(issuer != address(0), "CredentialVerification: Invalid issuer");
        require(_issuers[issuer], "CredentialVerification: Not an issuer");
        _issuers[issuer] = false;
        emit IssuerRemoved(issuer);
    }

    /// @notice Check if address is issuer
    /// @param issuer Address to check
    /// @return True if issuer
    function isIssuer(address issuer) external view returns (bool) {
        return _issuers[issuer];
    }

    // ============ Credential Management ============

    /// @notice Issue credential to student
    /// @param student Student address
    /// @param name Credential name
    /// @param level Credential level (e.g., "Bachelor", "Master")
    /// @param expirationDays Days until expiration (0 = no expiration)
    /// @param ipfsMetadata IPFS metadata URI
    /// @return tokenId The issued credential token ID
    function issueCredential(
        address student,
        string calldata name,
        string calldata level,
        uint256 expirationDays,
        string calldata ipfsMetadata
    ) external returns (uint256) {
        require(_issuers[msg.sender], "CredentialVerification: Not authorized to issue");
        require(student != address(0), "CredentialVerification: Invalid student");
        require(bytes(name).length > 0, "CredentialVerification: Name cannot be empty");
        require(bytes(level).length > 0, "CredentialVerification: Level cannot be empty");

        // Mint NFT
        uint256 tokenId = nftContract.mint(student, ipfsMetadata);

        // Store credential data
        uint256 expiresAt = 0;
        if (expirationDays > 0) {
            expiresAt = block.timestamp + (expirationDays * 1 days);
        }

        _credentials[tokenId] = Credential({
            name: name,
            level: level,
            issuer: msg.sender,
            issuedAt: block.timestamp,
            expiresAt: expiresAt,
            revoked: false,
            ipfsMetadata: ipfsMetadata
        });

        _userCredentials[student].push(tokenId);

        emit CredentialIssued(tokenId, student, name, level, msg.sender);
        return tokenId;
    }

    /// @notice Revoke credential
    /// @param tokenId Credential token ID
    function revokeCredential(uint256 tokenId) external {
        Credential storage cred = _credentials[tokenId];
        require(cred.issuer == msg.sender || msg.sender == owner(), "CredentialVerification: Not authorized");
        require(!cred.revoked, "CredentialVerification: Already revoked");

        cred.revoked = true;
        emit CredentialRevoked(tokenId, msg.sender);
    }

    /// @notice Check if credential is valid (not revoked and not expired)
    /// @param tokenId Credential token ID
    /// @return True if credential is valid
    function isCredentialValid(uint256 tokenId) public view returns (bool) {
        Credential storage cred = _credentials[tokenId];
        
        // Check existence
        if (!_exists(tokenId)) return false;
        
        // Check revocation
        if (cred.revoked) return false;
        
        // Check expiration
        if (cred.expiresAt > 0 && block.timestamp > cred.expiresAt) {
            return false;
        }
        
        return true;
    }

    /// @notice Verify credential
    /// @param tokenId Credential token ID
    /// @return valid True if credential is valid
    /// @return name Credential name
    /// @return level Credential level
    function verifyCredential(uint256 tokenId)
        external
        view
        returns (bool valid, string memory name, string memory level)
    {
        Credential storage cred = _credentials[tokenId];
        return (isCredentialValid(tokenId), cred.name, cred.level);
    }

    /// @notice Get credential details
    /// @param tokenId Credential token ID
    /// @return Credential struct
    function getCredential(uint256 tokenId) external view returns (Credential memory) {
        require(_exists(tokenId), "CredentialVerification: Token does not exist");
        return _credentials[tokenId];
    }

    /// @notice Get all credentials of user
    /// @param user User address
    /// @return Array of credential token IDs
    function getCredentialsByUser(address user) external view returns (uint256[] memory) {
        require(user != address(0), "CredentialVerification: Invalid user");
        return _userCredentials[user];
    }

    /// @notice Get active credentials of user
    /// @param user User address
    /// @return Array of valid credential token IDs
    function getActiveCredentialsByUser(address user) external view returns (uint256[] memory) {
        require(user != address(0), "CredentialVerification: Invalid user");
        
        uint256[] storage allCreds = _userCredentials[user];
        uint256 activeCount = 0;

        // Count active credentials
        for (uint256 i = 0; i < allCreds.length; i++) {
            if (isCredentialValid(allCreds[i])) {
                activeCount++;
            }
        }

        // Build array of active credentials
        uint256[] memory activeCreds = new uint256[](activeCount);
        uint256 index = 0;
        for (uint256 i = 0; i < allCreds.length; i++) {
            if (isCredentialValid(allCreds[i])) {
                activeCreds[index] = allCreds[i];
                index++;
            }
        }

        return activeCreds;
    }

    // ============ Utility Functions ============

    /// @notice Get credential statistics
    /// @return totalIssuers Total number of issuers
    /// @return totalCredentialVariants Total credential types
    function getStats() external view returns (uint256 totalIssuers, uint256 totalCredentialVariants) {
        return (0, 0); // Placeholder
    }

    /// @notice Internal function to check if token exists
    function _exists(uint256 tokenId) internal view returns (bool) {
        try nftContract.exists(tokenId) returns (bool exists_) {
            return exists_;
        } catch {
            return false;
        }
    }
}

